import React,{useContext} from 'react';
import {GlobalContext} from '../context/GlobalState'
import { useState } from 'react';

export default function Transectionlist () {

    let {transections} = useContext(GlobalContext)

    return(
        <>
        <h3>History</h3>
        <ul className="list">
         {transections.map(transection =>( <li className="minus">
            {transection.text} <span>{transection.amount}</span><button className="de lete-btn">x</button>
          </li>))}
        </ul>
        </>
    );
}
