import React from 'react';
import {GlobalContext} from '../context/GlobalState';

export default function Transaction(props){

 const {deletetransaction} = useContext(GlobalContext);

  const sign = props.transaction.amount < 0 ? '-':'+';



    return( <li className={props.transaction.amount < 0 ? 'minus':'plus'}>
    {props.transaction.text} <span>{sign}${Math.abs(props.transaction.amount)}</span>
    <button onClick={()=>deletetransaction(props.transaction.id)} className="de lete-btn">x</button>
  </li>
  );
}
